$(document).ready(function(){if(document.location.hash.length>0){var href=Base64.decode(document.location.hash);postLinkClick(href);}});function formatPostTiles(){initThumbHovers();$("#posts .posts-paginate-menu a").click(function(){postLinkClick($(this).attr("href"));document.location.hash=Base64.encode($(this).attr("href"));return false;});}function postLinkClick(url){$.ajax({url:url,success:function(d){$("#posts").html(d);formatPostTiles();}});}