selectBoxIt._applyNativeSelect=function(){var self=this,currentOption,currentDataText,currentText;self.dropdownContainer.append(self.selectBox);self.selectBox.css({display:"block",width:self.dropdown.outerWidth(),height:self.dropdown.outerHeight(),opacity:"0",position:"absolute",top:"0",left:"0",cursor:"pointer","z-index":"999999",margin:self.dropdown.css("margin"),padding:"0","-webkit-appearance":"menulist-button"}).bind({"changed.selectBoxIt":function(){currentOption=self.selectBox.find("option").filter(":selected");currentDataText=currentOption.attr("data-text");currentText=currentDataText?currentDataText:currentOption.text();self._setText(self.dropdownText,currentText);if(self.list.find('li[data-val="'+currentOption.val()+'"]').find("i").attr("class")){self.dropdownImage.attr("class",self.list.find('li[data-val="'+currentOption.val()+'"]').find("i").attr("class")).addClass("selectboxit-default-icon");}}});};selectBoxIt._mobile=function(callback){var self=this;if(self.options.isMobile()){self._applyNativeSelect();}return this;};